__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "AERIS rediscover at dawn or on online",
    "parent": 0,
    "comment": "Re-discover AERIS daily before sunrise or when node becomes online",
    "if": [
      { "at": { "sunrise": -900 } },
      { "or": 1 },
      { "==": { "device": "n010_controller", "status": "ST", "value": 1 } }
    ],
    "then": [
      { "device": "n010_controller", "command": "DISCOVER" }
    ],
    "else": [],
    "reasoning": "Schedule triggers a re-discover 15 minutes before sunrise daily; OR run immediately if the AERIS NodeServer Online (ST) becomes True to ensure the driver refreshes when it connects. Using DISCOVER as the accepted command for n010_controller."
  }
}
__END_NUCORE_ROUTINE__
