__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Night press or Welcome Guest COS activates Welcome Guest",
    "parent": 0,
    "comment": "Activate Welcome Guest when Bump in Night is pressed at night or Welcome Guest changes to True",
    "if": [
      { "(": 1 },
        { "==": { "device": "n005_task_4", "control": "ACTIVATE" } },
        { "or": 1 },
        { "==": { "device": "n005_task_3", "status": "ST", "value": 1, "uom": "2", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "n005_task_3", "command": "Activate", "parameters": [] }
    ],
    "else": [],
    "reasoning": "COC: pressing Bump in Night (control ACTIVATE) and COS: Welcome Guest status -> True are both valid triggers. Restrict to nightly hours using the from time 22:00 to 06:00 (day:1). The action calls the existing Activate command on the Welcome Guest device."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 2,
    "name": "Scheduled watering Tue/Thu/Sat if Welcome Guest off or manual activation",
    "parent": 0,
    "comment": "Run Water the Yard on schedule only when Welcome Guest is False or when Welcome Guest is manually activated",
    "if": [
      { "(": 1 },
        { "weekly": { "days": "tue,thu,sat", "from": { "time": "09:00", "for": { "hours": 2, "minutes": 0, "seconds": 0 } } } },
        { "or": 1 },
        { "==": { "device": "n005_task_3", "control": "ACTIVATE" } },
      { ")": 1 },
      { "and": 1 },
      { "==": { "device": "n005_task_3", "status": "ST", "value": 0, "uom": "2", "precision": 0 } }
    ],
    "then": [
      { "device": "n005_task_5", "command": "Activate", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Schedule triggers the weekly watering window (Tue/Thu/Sat at 09:00 for 2 hours). Allow manual override via COC (manual Activate of Welcome Guest). Only proceed if Welcome Guest status is False (ST == 0) to avoid overlapping guest tasks. Action activates the Water the Yard device."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 3,
    "name": "Post-sunset triggers activate Bump in Night and Welcome Guest",
    "parent": 0,
    "comment": "If Water the Yard turns on or Bump in Night is pressed at sunset+600s, activate both tasks",
    "if": [
      { "(": 1 },
        { "==": { "device": "n005_task_5", "status": "ST", "value": 1, "uom": "2", "precision": 0 } },
        { "or": 1 },
        { "==": { "device": "n005_task_4", "control": "ACTIVATE" } },
      { ")": 1 },
      { "and": 1 },
      { "at": { "sunset": 600 } }
    ],
    "then": [
      { "device": "n005_task_4", "command": "Activate", "parameters": [] },
      { "device": "n005_task_3", "command": "Activate", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Combine COS (Water the Yard status -> True) and COC (manual press of Bump in Night) with a schedule trigger at sunset + 600 seconds. When either event occurs at that time, activate both Bump in Night and Welcome Guest using their Activate commands."
  }
}
__END_NUCORE_ROUTINE__
