__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Fire sensor triggers waterbed on and close shade",
    "parent": 0,
    "comment": "COS on fire sensor -> safety actions",
    "if": [
      { "==": { "device": "30 46 14 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "1B 7F 7C 1", "command": "DON" },
      { "device": "ZY003_1", "command": "DOF" }
    ],
    "else": [],
    "reasoning": "A COS event where the Fire Sensor ST becomes On should immediately enable the waterbed and close the dining shade for occupant safety and to prepare the area for response teams. Actions use existing DON/DOF commands from the device definitions."
  }
}
__END_NUCORE_ROUTINE__
