__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Deck manual ON during evening: fix garage sensor responding",
    "parent": 0,
    "comment": "When deck transformer is turned on manually in the evening and the garage sensor is not responding, set config parameter and commit.",
    "if": [
      { "==": { "device": "ZY028_251", "control": "DON" } },
      { "and": 1 },
      { "weekly": { "days": "sun,mon,tue,wed,thu,fri,sat", "from": { "time": "19:00", "to": { "time": "22:00" } } } },
      { "and": 1 },
      { "==": { "device": "ZY031_1", "status": "ERR", "value": 1, "uom": "25", "precision": 0 } }
    ],
    "then": [
      { "device": "ZY031_1", "command": "CONFIG", "parameters": [ { "name": "NUM", "value": 5 }, { "name": "VAL", "value": 1, "uom": "107", "precision": 0 } ] },
      { "device": "ZY031_1", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger on physical ON (DON) of the deck transformer during evening hours. Check the Old Garage Multisensor Responding enum (uom 25). If it's False (1), set configuration parameter 5 to 1 using 1-byte unsigned (uom 107) and write changes to apply the configuration."
  }
}
__END_NUCORE_ROUTINE__
