__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday morning physical ON triggers identify",
    "parent": 0,
    "comment": "Run identify for 10s when the kitchen floor is physically turned on during weekday mornings and the multisensor is responding.",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZB19950_001_1", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "ZB19950_001_1", "control": "TOGGLE" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:00", "to": { "time": "08:00" } } } },
      { "and": 1 },
      { "==": { "device": "ZY171_1", "status": "ERR", "value": 0, "uom": "25", "precision": 0 } }
    ],
    "then": [
      { "device": "ZB19950_001_1", "command": "IDENTIFY", "parameters": [ { "name": "n/a", "value": 10, "uom": "58", "precision": 0 } ] }
    ],
    "else": [
    ],
    "reasoning": "Detect a physical ON/TOGGLE on the kitchen floor switch (COC). Restrict to weekday mornings using the weekly schedule. Ensure the MultiSensor is currently responding (ERR == True) before running IDENTIFY for 10 seconds to help locate the switch or provide feedback."
  }
}
__END_NUCORE_ROUTINE__
