__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Dimmer FastOn -> Kitchen 100% evenings",
    "parent": 0,
    "comment": "When the ZY 218 dimmer receives a physical Fast On (COC) during evening hours, set Kitchen Floor 2 to 100%",
    "if": [
      { "==": { "device": "ZY218_1", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "sun,mon,tue,wed,thu,fri,sat", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "ZB29723_001_1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (DFON) from the ZY 218 dimmer and restrict to evening hours via a weekly schedule. Then send DON to the Kitchen Floor device with the 0-100% parameter (uom 51) set to 100 to ensure full brightness."
  }
}
__END_NUCORE_ROUTINE__
