__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night motion or tamper triggers home query",
    "parent": 0,
    "comment": "Query home sensor when motion detected or tamper activated at night",
    "if": [
      { "(": 1 },
        { "==": { "device":"ZY031_155","status":"ST","value":100,"uom":"78","precision":0 } },
        { "or": 1 },
        { "==": { "device":"ZY031_157","control":"DON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } }
    ],
    "then": [
      { "device":"ZY031_308","command":"QUERY","parameters":[] }
    ],
    "else": [],
    "reasoning": "During nighttime (22:00–06:00) motion (COS: Motio -> On) or a manual tamper ON (COC: Tampe DON) likely indicates activity; querying the Home device refreshes alarm status for monitoring."
  }
}
__END_NUCORE_ROUTINE__
