__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Undercounter 50% when 1st island manually turned on evening",
    "parent": 0,
    "comment": "Set undercounter level and ramp when first island is manually turned on in the evening",
    "if": [
      { "(": 1 },
        { "==": { "device": "42 EF C5 1", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "42 EF C5 1", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "42 E1 C4 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 50, "uom": "51", "precision": 0 } ] },
      { "device": "42 E1 C4 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 18, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Group COC checks for DFON or DON on Kitchen_isld_1st and require local evening schedule. Then set On Level (OL) to 50% using uom 51 and set Ramp Rate (RR) to enum value 18 (30.0 seconds) on Kitchen_undercounter. No else actions defined here." 
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Undercounter off when 1st island turned off evening",
    "parent": 0,
    "comment": "Turn undercounter off if first island is turned off during the same evening window",
    "if": [
      { "(": 1 },
        { "==": { "device": "42 EF C5 1", "control": "DFOF" } },
        { "or": 1 },
        { "==": { "device": "42 EF C5 1", "control": "DOF" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "42 E1 C4 1", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Listen for manual off controls (DFOF or DOF) on Kitchen_isld_1st during the same evening window and turn Kitchen_undercounter off using DOF to mirror the manual control." 
  }
}
__END_NUCORE_ROUTINE__
