__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Garage: Active cooling during weekday heat or manual cool",
    "parent": 0,
    "comment": "When hot during work hours or when Cool Ctl is manually turned on, enforce aggressive cooling; otherwise relax setpoint and use Auto mode",
    "if": [
      { "(": 1 },
        { ">": { "device": "50 59 A7 1", "status": "ST", "value": 850, "uom": "14", "precision": 1 } },
        { "and": 1 },
        { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } },
      { ")": 1 },
      { "or": 1 },
      { "==": { "device": "50 59 A7 2", "control": "DON" } }
    ],
    "then": [
      { "device": "50 59 A7 1", "command": "CLISPC", "parameters": [ { "name": "n/a", "value": 700, "uom": "14", "precision": 1 } ] },
      { "device": "50 59 A7 1", "command": "CLIMD", "parameters": [ { "name": "n/a", "value": 2, "uom": "98" } ] }
    ],
    "else": [
      { "device": "50 59 A7 1", "command": "CLISPC", "parameters": [ { "name": "n/a", "value": 720, "uom": "14", "precision": 1 } ] },
      { "device": "50 59 A7 1", "command": "CLIMD", "parameters": [ { "name": "n/a", "value": 3, "uom": "98" } ] }
    ],
    "reasoning": "Use the thermostat's temperature (ST) with precision 1 (value=degrees*10). During weekday work hours if temperature exceeds 85.0°F, or if the Cool control is manually activated (COC DON), set aggressive cooling (70°F and Cool mode). Otherwise relax to 72°F and Auto mode to save energy. Actions target the Main thermostat which accepts CLISPC and CLIMD commands."
  }
}
__END_NUCORE_ROUTINE__
