__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Query sensors when moisture On during weekdays",
    "parent": 0,
    "comment": "",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY016_359", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
        { "or": 1 },
        { "==": { "device": "ZY016_359", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "ZY042_1", "command": "QUERY" },
      { "device": "ZY016_118", "command": "QUERY" }
    ],
    "else": [],
    "reasoning": "Trigger when moisture sensor changes to On (COS) or is physically turned On (COC) during weekdays 09:00-17:00; then query WinkNW and multilevel sensor to refresh state."
  }
}
__END_NUCORE_ROUTINE__
