    "id": 101,
    "name": "Landscape fast-on after sunset -> D4 on + D5 backlight 50%",
    "parent": 0,
    "comment": "",
    "if": [
      { "==": { "device": "13 FB 91 1", "control": "DFON" } },
      { "and": 1 },
      { "at": { "sunset": 600 } }
    ],
    "then": [
      { "device": "14 43 75 1", "command": "DON", "parameters": [] },
      { "device": "14 43 F2 1", "command": "BL", "parameters": [ { "name": "n/a", "value": 50, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "When Landscape receives a fast-on control near sunset (10 minutes after), enable main pathway light D4 and set D5 backlight to 50% for safe visibility."
  }
}
__END_NUCORE_ROUTINE__
