__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night tamper trigger queries security and power alarms",
    "parent": 0,
    "comment": "Query devices when tamper status changes to On overnight",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY085_157", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY085_308", "command": "QUERY" },
      { "device": "ZY085_309", "command": "QUERY" }
    ],
    "else": [],
    "reasoning": "At night, a Tamper Alarm changing to On likely indicates an intrusion; query Home Security and Power Alarm devices for status."
  }
}
__END_NUCORE_ROUTINE__
