__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Entry motion turns on Pool lights 18-23",
    "parent": 0,
    "comment": "Turn on pool lights when entry motion occurs during evening window",
    "if": [
      { "==": { "device": "n006_31920912_m", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "n006_32529166_lt", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "User requested Pool lights turn on when Entry detects motion during the evening. The routine triggers on the motion send command (== DON) from the Entry device and requires the time window 18:00-23:00. Action uses the Pool device's accepted Turn on (DON) command."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Turn off Pool lights at 23:00 daily",
    "parent": 0,
    "comment": "Ensure Pool lights are turned off every night at 23:00",
    "if": [
      { "at": { "time": "23:00" } }
    ],
    "then": [
      { "device": "n006_32529166_lt", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "User requested Pool lights off at 23:00. This standalone scheduled routine triggers at 23:00 and sends the Pool device its accepted Turn off (DOF) command."
  }
}
__END_NUCORE_ROUTINE__
