__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "C-D DFON or E-F DON -> set G-H On during business hours",
    "parent": 0,
    "comment": "Trigger G-H to On when either remote control is pressed during weekdays",
    "if": [
      { "(": 1 },
        { "==": { "device": "54 E9 8A 2", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "54 E9 8A 3", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "54 E9 8A 4", "command": "WDU", "parameters": [ { "name": "ST", "value": 100, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use COC triggers (DFON or DON) scoped to weekdays 09:00-17:00; action writes ST=100 via WDU to device G-H which accepts WDU."
  }
}
__END_NUCORE_ROUTINE__
