__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "DFON -> Living Room 6: warm 3000K @ brightness 200",
    "parent": 0,
    "comment": "Triggered by Fast On (DFON) control on Living Room 6. Sets color temperature and brightness with a 2s transition.",
    "if": [
      { "==": { "device": "n001_88010d0956720b", "control": "DFON" } }
    ],
    "then": [
      {
        "device": "n001_88010d0956720b",
        "command": "SET_CTBR",
        "parameters": [
          { "name": "K", "value": 3000, "uom": "26", "precision": 1 },
          { "name": "D", "value": 2000, "uom": "42", "precision": 1 },
          { "name": "BR", "value": 200, "uom": "100", "precision": 1 }
        ]
      }
    ],
    "else": [],
    "reasoning": "Use DFON COC to immediately apply a scene. SET_CTBR atomically sets color temperature and brightness with a specified transition (D). Brightness precision 1 and K precision 1 match device definitions."
  }
}
__END_NUCORE_ROUTINE__
