__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night fountain trigger sets Office 4 warm dim",
    "parent": 0,
    "comment": "When fountain fast-on overnight set Office 4 temp and brightness",
    "if": [
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } },
      { "and": 1 },
      { "==": { "device": "27 FE EA 1", "control": "DFON" } }
    ],
    "then": [
      {
        "device": "n001_88010d09c6030b",
        "command": "SET_CTBR",
        "parameters": [
          { "name": "Color Temperature", "value": 2700, "uom": "26", "precision": 1 },
          { "name": "Transition Time", "value": 2000, "uom": "42", "precision": 1 },
          { "name": "Brightness", "value": 127, "uom": "100", "precision": 1 }
        ]
      }
    ],
    "else": [],
    "reasoning": "Use COC (fast on) from the Yard Fountain but only overnight (22:00-06:00 day wrap). Then set Office 4 to a warm color temperature (2700K) at a medium brightness with a 2s transition to avoid abrupt lighting changes."
  }
}
__END_NUCORE_ROUTINE__
