__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night intrusion trigger queries",
    "parent": 0,
    "comment": "Query security devices when intrusion alarm activates overnight",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY002_172", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
        { "or": 1 },
        { "==": { "device": "ZY002_172", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY002_308", "command": "QUERY", "parameters": [] },
      { "device": "ZY002_306", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Monitor intrusion alarm state change to On or a manual On control (DON) during overnight hours; when triggered, query the home security and access control alarm devices to capture current statuses."
  }
}
__END_NUCORE_ROUTINE__
