__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "When audio starts set speaker and volume",
    "parent": 0,
    "comment": "",
    "if": [
      { "==": { "device": "n015_0", "status": "ST", "value": 1, "uom": "25", "precision": 0 } }
    ],
    "then": [
      { "device": "n015_0", "command": "VOLUME", "parameters": [ { "name": "n/a", "value": 30, "uom": "51", "precision": 2 } ] },
      { "device": "n015_0", "command": "OUTPUT", "parameters": [ { "name": "n/a", "value": 0, "uom": "25", "precision": 0 } ] }
    ],
    "else": [
      { "device": "n015_0", "command": "STOP", "parameters": [] },
      { "device": "n015_0", "command": "VOLUME", "parameters": [ { "name": "n/a", "value": 0, "uom": "51", "precision": 2 } ] }
    ],
    "reasoning": "Trigger on Playing Status (ST=1) as a COS when playback begins. Set output to Speaker Jack and a moderate volume. When not playing ensure playback stopped and volume muted to avoid noise."
  }
}
__END_NUCORE_ROUTINE__
