__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1001,
    "name": "Holiday_In-3 on at sunset",
    "parent": 0,
    "comment": "Turn Holiday_In-3 on at sunset daily",
    "if": [
      { "at": { "sunset": 0 } }
    ],
    "then": [
      { "device": "40 1B 26 1", "command": "DON" }
    ],
    "else": [],
    "reasoning": "Schedule trigger at local sunset to automatically enable Holiday_In-3 every day. Using device accept command DON which exists on the device."
  }
}
__END_NUCORE_ROUTINE__
__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1002,
    "name": "Holiday_In-3 off at 23:00",
    "parent": 0,
    "comment": "Turn Holiday_In-3 off at 23:00 daily",
    "if": [
      { "at": { "time": "23:00" } }
    ],
    "then": [
      { "device": "40 1B 26 1", "command": "DOF" }
    ],
    "else": [],
    "reasoning": "Scheduled off action at 23:00 to ensure holiday lights are turned off nightly using DOF command defined for the device."
  }
}
__END_NUCORE_ROUTINE__
