__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Notify and keep awake when ZY171 sensor turns On on weekdays",
    "parent": 0,
    "comment": "When BinaryiSensor turns On during weekdays notify UD Mobile and enable Keep Awake for 2 hours",
    "if": [
      { "==": { "device": "ZY171_104", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "n003_udmobile", "command": "GV10", "parameters": [ { "name": "Group", "value": 1, "uom": "25", "precision": 0 }, { "name": "Sound", "value": 1, "uom": "25", "precision": 0 } ] },
      { "device": "ZY171_199", "command": "SETIV", "parameters": [ { "name": "n/a", "value": 2, "uom": "20", "precision": 0 } ] },
      { "device": "ZY171_199", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "78", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Trigger when the BinaryiSensor changes to On (COS) during weekday business hours; send a notification via UD Mobile and set the ZY171 wakeup to Keep Awake with a 2-hour wake interval."
  }
}
__END_NUCORE_ROUTINE__
