__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Dry ON at Sunset -> Query Dry & Heartbeat",
    "parent": 0,
    "comment": "Query status after Dry turns on at sunset",
    "if": [
      { "==": { "device": "3F AB 1E 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "at": { "sunset": 0 } }
    ],
    "then": [
      { "device": "3F AB 1E 1", "command": "QUERY" },
      { "device": "3F AB 1E 4", "command": "QUERY" }
    ],
    "else": [],
    "reasoning": "When the Dry unit reports a COS to ON at sunset, immediately query the Dry and the Heartbeat devices to confirm operational state and responsiveness."
  }
}
__END_NUCORE_ROUTINE__
