__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night paddle fast-on sets low dim and fastest ramp",
    "parent": 0,
    "comment": "When physical Fast On (DFON) occurs during the night window, set low on-level and fastest ramp rate",
    "if": [
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } },
      { "and": 1 },
      { "==": { "device": "61 84 A0 1", "control": "DFON" } }
    ],
    "then": [
      { "device": "61 84 A0 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 30, "uom": "51", "precision": 0 } ] },
      { "device": "61 84 A0 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 31, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Limit response to the physical Fast On control at night to avoid bright transitions: set On Level to 30% and Ramp Rate enum index 31 (0.1s) for the fastest ramp. Uses DFON COC and a midnight-spanning time window."
  }
}
__END_NUCORE_ROUTINE__
