__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: Turn on walkway lights if garage responding",
    "parent": 0,
    "comment": "At sunset, require garage Responding == True; otherwise alert and only enable FE",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "40 E7 63 1", "status": "ERR", "value": 0, "uom": "25", "precision": 0 } }
    ],
    "then": [
      { "device": "40 E6 CA 1", "command": "DON" },
      { "device": "38 75 B8 1", "command": "DON" }
    ],
    "else": [
      { "device": "40 E7 63 1", "command": "BEEP" },
      { "device": "40 E6 CA 1", "command": "DON" }
    ],
    "reasoning": "Use sunset schedule and check the Garage 'Responding' property (ERR==0 True). If responding, turn on both walkway lights. If not responding, emit a beep to notify and only enable FE to avoid relying on an unresponsive Garage device."
  }
}
__END_NUCORE_ROUTINE__
