__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Underheat Alarm ON -> White ON overnight",
    "parent": 0,
    "comment": "Turn White on when underheat alarm turns on between 22:00-06:00",
    "if": [
      { "==": { "device": "ZY016_168", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY006_004_1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger on change-of-state of ZY016_168 to On (value 100). Restrict to overnight window 22:00-06:00. Then send On (DON) to White (ZY006_004_1)."
  }
}
__END_NUCORE_ROUTINE__
__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 2,
    "name": "Underheat Alarm OFF -> White OFF overnight",
    "parent": 0,
    "comment": "Turn White off when underheat alarm turns off between 22:00-06:00",
    "if": [
      { "==": { "device": "ZY016_168", "status": "ST", "value": 0, "uom": "78", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY006_004_1", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger on change-of-state of ZY016_168 to Off (value 0). Restrict to the same overnight window. Then send Off (DOF) to White."
  }
}
__END_NUCORE_ROUTINE__
