__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Query Weather Alarm and Underheat when Too Damp ON during weekdays",
    "parent": 0,
    "comment": "On Too Damp->On during weekday business hours, collect states from Weather Alarm and Underheat",
    "if": [
      { "==": { "device": "ZY075_359", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "ZY075_317", "command": "QUERY", "parameters": [] },
      { "device": "ZY075_168", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger on Too Damp changing to ON during defined weekday hours; then query Weather Alarm and Underheat so the system has current states for downstream automation or notifications."
  }
}
__END_NUCORE_ROUTINE__
