__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday evening scene button -> Booster On + Keep Awake",
    "parent": 0,
    "comment": "When the scene button fast-on is used (or Booster is already On) during weekday evenings, enable Booster and Keep Awake",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY024_201N001", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "ZY024_1", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "ZY024_1", "command": "DON" },
      { "device": "ZY043_199", "command": "DON" }
    ],
    "else": [],
    "reasoning": "Use a grouped trigger: either a fast-on control (DFON) from the scene button or existing Booster On state, limited to weekday evenings. Actions turn the Booster on and enable Keep Awake on the WinkSE device. No parameters required for these commands."
  }
}
__END_NUCORE_ROUTINE__
