__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday morning DFON: Bedroom 30% and Steve full",
    "parent": 0,
    "comment": "Triggered by DFON on Steve's Table Lamp during weekdays 06:00-09:00",
    "if": [
      { "(": 1 },
        { "==": { "device": "15 8E CD 1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days":"mon,tue,wed,thu,fri", "from": { "time":"06:00", "to": { "time":"09:00" } } } }
    ],
    "then": [
      { "device":"15 8D 96 1", "command":"DON", "parameters":[{ "name":"n/a","value":30,"uom":"51","precision":0 }] },
      { "device":"15 8E CD 1", "command":"OL", "parameters":[{ "name":"n/a","value":100,"uom":"51","precision":0 }] }
    ],
    "else": [],
    "reasoning": "Use a COC (DFON) from Steve's Table Lamp restricted to weekday mornings; then set Bedroom Candles to 30% (DON with uom 51) and set Steve's On Level to 100% using the OL command."
  }
}
__END_NUCORE_ROUTINE__
