__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night tamper ON triggers queries",
    "parent": 0,
    "comment": "When Tamper Alarm sends DON during night, query other alarms",
    "if": [
      { "==": { "device":"ZY085_157","control":"DON" } },
      { "and": 1 },
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } }
    ],
    "then": [
      { "device":"ZY085_308","command":"QUERY","parameters":[] },
      { "device":"ZY085_309","command":"QUERY","parameters":[] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (Tamper Alarm sends DON) scoped to night hours (22:00-06:00). When triggered, query Home Security and Power Alarm to collect their current states for incident handling."
  }
}
__END_NUCORE_ROUTINE__
