__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "LOffice fast-on -> full on + 1min ramp",
    "parent": 0,
    "comment": "Trigger from physical fast-on on child switch B",
    "if": [
      { "==": { "device": "60 47 BF 2", "control": "DFON" } }
    ],
    "then": [
      { "device": "60 47 BF 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ] },
      { "device": "60 47 BF 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use COC from device 60 47 BF 2 DFON to immediately set the parent LOffice to full brightness (100% uom 51) and set Ramp Rate to the explicit enum value for 1.0 minute (12)."
  }
}
__END_NUCORE_ROUTINE__
