__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Overnight double-tap starts fan + vanity",
    "parent": 0,
    "comment": "Double-tap closet light between 22:00–06:00 to run fan and set vanity to 50%",
    "if": [
      { "==": { "device": "3E D 5C 1", "control": "DFON", "parameters": [ { "name": "tapCount", "value": 2, "uom": "108", "precision": 0 } ] } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "51 2 B6 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 2 } ] },
      { "device": "1C 86 FB 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 50, "uom": "51", "precision": 2 } ] }
    ],
    "else": [],
    "reasoning": "Trigger is a COC (double-tap DFON) on Closet Light during an overnight schedule. Then: send DON to Master Bath Fan with parameter 100 (On) and set Master Bath Vanity to 50% using its DON parameter. Uses device IDs and UOMs from DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
