__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset dim ZY006_119 if Heat3 off",
    "parent": 0,
    "comment": "Set dimmer to 75% at sunset only when Heat 3 reports Off",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "n003_d4c2bd7d", "status": "ST", "value": 0, "uom": "78", "precision": 0 } }
    ],
    "then": [
      {
        "device": "ZY006_119",
        "command": "DON",
        "parameters": [
          { "name": "n/a", "value": 75, "uom": "51", "precision": 0 }
        ]
      }
    ],
    "else": [],
    "reasoning": "At local sunset, set ZY006_119 to 75% using DON parameter (uom 51) only when Heat 3 state equals Off (0, uom 78). This avoids turning lights on when heat indicates active use."
  }
}
__END_NUCORE_ROUTINE__
