__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Night motion triggers power and tamper poll",
    "parent": 0,
    "comment": "Query alarms when motion is detected or controlled during night hours",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY019_155", "control": "DON" } },
        { "or": 1 },
        { ">=": { "device": "ZY019_155", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY019_309", "command": "QUERY", "parameters": [] },
      { "device": "ZY019_157", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "During overnight hours (22:00-06:00) any physical activation (DON) or state change (ST >= On) of the motion sensor should poll the power and tamper alarms for status."
  }
}
__END_NUCORE_ROUTINE__
