__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Motion-triggered pool lights after sunset",
    "parent": 0,
    "comment": "Turn on pool lights when Entry or Patio detect motion after sunset until 23:00",
    "if": [
      { "(": 1 },
        { "==": { "device": "n006_31920912_m", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "n006_23500682_m", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "sunset": 0, "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "n006_32529166_lt", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "When either motion sensor sends a motion event (COC) during evening hours (from sunset until 23:00), enable pool lights to illuminate the area." 
  }
}
__END_NUCORE_ROUTINE__
