__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Navigation Enter toggles Zone2 mute when AVR online, else power AVR on",
    "parent": 0,
    "comment": "",
    "if": [
      { "==": { "device": "n010_d_11", "control": "enter" } },
      { "and": 1 },
      { "==": { "device": "n010_d_12", "status": "ST", "value": 1, "uom": "2", "precision": 0 } }
    ],
    "then": [
      { "device": "n010_d_13", "command": "toggle_mute", "parameters": [] }
    ],
    "else": [
      { "device": "n010_d_12", "command": "power", "parameters": [] }
    ],
    "reasoning": "When the Navigation Enter control is activated (COC) and the ONKYO AVR reports Online (ST==1), toggle Zone2 mute; if the AVR is not online, issue a power command to the AVR to attempt to turn it on."
  }
}
__END_NUCORE_ROUTINE__
