__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening Fast-On cascade: NW -> NE+SE",
    "parent": 0,
    "comment": "When NW receives a Fast On control during evening hours, turn on NE and SE",
    "if": [
      { "==": { "device": "37 59 8A 1", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "sun,mon,tue,wed,thu,fri,sat", "from": { "time": "19:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "71 B A5 1", "command": "DON" },
      { "device": "2C F7 16 1", "command": "DON" }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (DFON) on the NW device so the routine responds to the physical Fast On action. Constrain with a weekly time window 19:00-23:00. Actions send DON to the two target landscape lights which accept DON. No parameters required."
  }
}
__END_NUCORE_ROUTINE__
