__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Fireplace fast-on during evening -> plants on, pool off",
    "parent": 0,
    "comment": "When LR is physically fast-turned on in the evening, make plants visible and ensure pool light is off.",
    "if": [
      { "==": { "device": "4F 21 4A 1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "sunset": 0, "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "33 EC C4 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ] },
      { "device": "40 F5 2C 1", "command": "DOF" }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (DFON) on the LR device to detect physical turn-on. Limit to the evening window (sunset -> 23:00). Then send DON=100 to Kitchen Plant Shelf LED (uses uom 51 enum) and DOF to Backyard pool light to ensure it's off."
  }
}
__END_NUCORE_ROUTINE__
