__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday morning remote DFON -> Gary 30% + RR 1min",
    "parent": 0,
    "comment": "",
    "if": [
      { "==": { "device": "1C 44 CB 2", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "07:00", "to": { "time": "09:00" } } } }
    ],
    "then": [
      { "device": "14 8C 36 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 30, "uom": "51", "precision": 2 } ] },
      { "device": "14 8C 36 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Trigger on the MBR remote sending DFON during the weekday morning window; then set Gary RM: Fan-Light ON to 30% (DON with percent) and set Ramp Rate to 1.0 minute (RR enum value 12)."
  }
}
__END_NUCORE_ROUTINE__
