__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Fast-on Outside -> Garage full (evening)",
    "parent": 0,
    "comment": "When Outside Lights DFON and time between 18:00-23:30, set Garage to 100%",
    "if": [
      { "==": { "device": "23 CD 6B 5", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:30", "day": 0 } } }
    ],
    "then": [
      { "device": "22 64 FF 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (Fast On DFON) from Outside Lights and restrict by a daily time window (18:00-23:30). If both true, send On (DON) to Garage Inside Lights at 100% using the accepted DON command and percent uom 51." 
  }
}
__END_NUCORE_ROUTINE__
