__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Evening arrival: warm group3 on or off at night",
    "parent": 0,
    "comment": "When office plug or manual fast-on indicates arrival during evening window, set warm white scene; otherwise ensure group off at night.",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY010_1", "status": "ST", "value": 100, "uom": "78" } },
        { "or": 1 },
        { "==": { "device": "n001_huegrp3", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      {
        "device": "n001_huegrp3",
        "command": "SET_CTBR",
        "parameters": [
          { "name": "K", "value": 3000, "uom": "26", "precision": 1 },
          { "name": "D", "value": 2000, "uom": "42", "precision": 1 },
          { "name": "BR", "value": 200, "uom": "100", "precision": 1 }
        ]
      }
    ],
    "else": [
      { "device": "n001_huegrp3", "command": "DOF", "parameters": [] }
    ],
    "reasoning": "Use COS on Office Plug (status becomes On) or COC (fast-on) on group3 during the defined evening window to trigger a warm white scene via SET_CTBR with safe values from device schema; otherwise ensure group3 is off at night."
  }
}
__END_NUCORE_ROUTINE__
