__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Night intrusion ON triggers query of access and home security alarms",
    "parent": 0,
    "comment": "Query subordinate alarms when intrusion becomes ON or is manually turned ON overnight",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY002_172", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
        { "or": 1 },
        { "==": { "device": "ZY002_172", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY002_306", "command": "QUERY", "parameters": [] },
      { "device": "ZY002_308", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Listen for COS (status -> On) or COC (manual On control) from the Intrusion Alarm (ZY002_172) within the overnight window; when triggered, request current states from the Access Control (ZY002_306) and Home Security (ZY002_308) modules so the system has up-to-date context for alarms overnight."
  }
}
__END_NUCORE_ROUTINE__
