__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Heartbeat On during weekdays - query and write changes when not responding",
    "parent": 0,
    "comment": "When Heartbeat becomes On during work hours and is not responding, query it and write changes",
    "if": [
      { "(": 1 },
      { "==": { "device": "2A FF 3C 4", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "==": { "device": "2A FF 3C 4", "status": "ERR", "value": 1, "uom": "25", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "08:00", "to": { "time": "18:00" } } } }
    ],
    "then": [
      { "device": "2A FF 3C 4", "command": "QUERY", "parameters": [] },
      { "device": "2A FF 3C 4", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Only act when Heartbeat transitions to On while it simultaneously reports not responding during weekday business hours. Query and write changes to attempt recovery."
  }
}
__END_NUCORE_ROUTINE__
