__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night keypad activation: Output-004 for 120s",
    "parent": 0,
    "comment": "Activate Output-004 for 120 seconds when either keypad registers a user code during overnight hours",
    "if": [
      { "(": 1 },
        { "==": { "device": "n005_keypad_1", "control": "GV10" } },
        { "or": 1 },
        { "==": { "device": "n005_keypad_2", "control": "GV10" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "n005_output_4", "command": "DON_WTIME", "parameters": [ { "name": "n/a", "value": 120, "uom": "58", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use COC from either keypad (GV10 = User Code Entered) as the trigger, constrained to the overnight schedule 22:00-06:00. When triggered, send DON_WTIME to Output-004 for 120 seconds using the device's defined time UOM (58). Parent is root (0)."
  }
}
__END_NUCORE_ROUTINE__
