__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Output-013 overnight manual on -> timed 120s",
    "parent": 0,
    "comment": "When Output-013 is manually turned on between 22:00-06:00, start a 120s timed on",
    "if": [
      { "==": { "device": "n005_output_13", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "n005_output_13", "command": "DON_WTIME", "parameters": [ { "name": "n/a", "value": 120, "uom": "58", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Primary routine: detect manual DON (COC) during overnight window and send a timed turn-on for 120 seconds using DON_WTIME.">
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Output-013 overnight manual on -> set default to 120 if zero",
    "parent": 0,
    "comment": "If Default On Seconds is 0 at manual overnight activation, set it to 120 and start timed on",
    "if": [
      { "==": { "device": "n005_output_13", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } },
      { "and": 1 },
      { "==": { "device": "n005_output_13", "status": "TIME", "value": 0, "uom": "58", "precision": 0 } }
    ],
    "then": [
      { "device": "n005_output_13", "command": "SET_TIME", "parameters": [ { "name": "n/a", "value": 120, "uom": "58", "precision": 0 } ] },
      { "device": "n005_output_13", "command": "DON_WTIME", "parameters": [ { "name": "n/a", "value": 120, "uom": "58", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Secondary routine: only when Default On Seconds equals 0, update it to 120 and then start the same 120s timed on so behavior matches the new default." 
  }
}
__END_NUCORE_ROUTINE__
