__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Overnight manual on -> shutoff if Shop sensor not responding",
    "parent": 0,
    "comment": "When Kitchen Floor is manually turned on overnight and Shop multisensor reports Responding=False, power off and identify for diagnostics",
    "if": [
      { "==": { "device": "ZB19950_001_1", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } },
      { "and": 1 },
      { "==": { "device": "ZY171_1", "status": "ERR", "value": 1, "uom": "25", "precision": 0 } }
    ],
    "then": [
      { "device": "ZB19950_001_1", "command": "DOF", "parameters": [] },
      { "device": "ZB19950_001_1", "command": "IDENTIFY", "parameters": [ { "name": "n/a", "value": 10, "uom": "58", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "A change-of-control (manual ON) on the kitchen floor during the overnight period combined with the Shop multisensor reporting Responding=False indicates a potential fault or unintended power state; immediately turn the circuit off to stop load and run IDENTIFY for 10s to aid physical diagnosis."
  }
}
__END_NUCORE_ROUTINE__
