__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1001,
    "name": "Evening DFON on E writes to H",
    "parent": 0,
    "comment": "When MiniRemote - E receives DFON on weekdays 18:00-23:00, trigger WDU on MiniRemote - H",
    "if": [
      { "==": { "device": "3F 0 2B 6", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "3F 0 2B 7", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "A COC (DFON) on MiniRemote - E combined with a weekday evening schedule should trigger a Write Changes (WDU) command to MiniRemote - H. WDU is the only accept command defined, so it's used without parameters.">
  }
}
__END_NUCORE_ROUTINE__
