__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sync E when F DFON or H DON during weekdays",
    "parent": 0,
    "comment": "When F fast-on or H on is used during workweek business hours, write same change to E.",
    "if": [
      {"(": 1},
        {"==": {"device": "26 33 AB 5", "control": "DFON"}},
        {"or": 1},
        {"==": {"device": "26 33 AB 7", "control": "DON"}},
      {")": 1},
      {"and": 1},
      {"weekly": {"days": "mon,tue,wed,thu,fri", "from": {"time": "08:00", "to": {"time": "18:00"}}}}
    ],
    "then": [
      {"device": "26 33 AB 6", "command": "WDU", "parameters": []}
    ],
    "else": [],
    "reasoning": "Group the two COC triggers (DFON from device F, DON from device H) and restrict to a weekday schedule 08:00–18:00; action uses the device's accepted command WDU on Mini Remote - E. No parameter names provided in DEVICE STRUCTURE, so WDU is sent without parameters."
  }
}
__END_NUCORE_ROUTINE__
