__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Bench Light DFON on weekdays keeps Wake Up awake",
    "parent": 0,
    "comment": "Keep ZY 011 Wake Up awake when bench light fast-on during work hours",
    "if": [
      { "==": { "device": "38 75 EE 1", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "ZY011_199", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "78", "precision": 0 } ] }
    ],
    "else": [
      { "device": "ZY011_199", "command": "DOF", "parameters": [] }
    ],
    "reasoning": "When the Shop Office Bench Light is fast-turned on (DFON) during weekday work hours (09:00-17:00), send DON to ZY011_199 with parameter On (100) to set Keep Awake. If the event occurs outside that schedule, send DOF to disable Keep Awake. This maps the COC trigger + weekly schedule to explicit device commands available in the DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
