__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Query other controllers when one is turned on",
    "parent": 0,
    "comment": "COC trigger: when any controller emits DON, poll the other controllers",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY016_252N003", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "ZY016_252N004", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "ZY016_252N005", "control": "DON" } },
      { ")": 1 }
    ],
    "then": [
      { "device": "ZY016_252N004", "command": "QUERY" },
      { "device": "ZY016_252N005", "command": "QUERY" }
    ],
    "else": [],
    "reasoning": "When any controller is turned on (DON), query the other two devices to refresh their states. Actions use only the 'Query' command which these devices accept."
  }
}
__END_NUCORE_ROUTINE__
