__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Weekday morning MBR ON triggers sensor update and queries",
    "parent": 0,
    "comment": "When either MBR switch is physically turned on during weekday mornings",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY054_252N003", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "ZY054_252N004", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "07:00", "to": { "time": "09:00" } } } }
    ],
    "then": [
      { "device": "ZY054_252N003", "command": "QUERY", "parameters": [] },
      { "device": "ZY054_252N004", "command": "QUERY", "parameters": [] },
      { "device": "ZY035_1", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Group COC from either MBR switch with a weekday morning window to ensure queries and a sensor write occur only when a physical ON action happens during commute hours."
  }
}
__END_NUCORE_ROUTINE__
