__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1001,
    "name": "Night: binary motion arms controller",
    "parent": 0,
    "comment": "Arm controller when binary sensor reports motion overnight",
    "if": [
      { "from": { "time":"22:00", "to":{ "time":"06:00","day":1 } } },
      { "and": 1 },
      { "==": { "device":"ZY044_380", "status":"ST", "value":100, "uom":"78", "precision":0 } }
    ],
    "then": [
      { "device":"ZY044_1", "command":"CONFIG", "parameters":[ { "name":"NUM", "value":1, "precision":0 }, { "name":"VAL", "value":1, "uom":"107", "precision":0 } ] }
    ],
    "else": [],
    "reasoning": "Trigger when the binary motion sensor's ST becomes On (100) during the overnight period (22:00-06:00). The WinkNE controller accepts CONFIG, so set Parameter Number=1 and Parameter Value=1 (1-byte uom 107) to arm the system."
  }
}
__END_NUCORE_ROUTINE__
